INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172732, 'Fluvastatin', 'Toremifene', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of fluvastatin. According to the prescribing information, fluvastatin is primarily metabolized by CYP450 2C9 (approximately 75%) and, to a much lesser extent, by CYP450 2C8 and 3A4 (approximately 5% and 20%, respectively).', NULL, 'Caution is advised when fluvastatin is prescribed with CYP450 2C9 inhibitors.', NULL, 'Caution is advised when fluvastatin is prescribed with CYP450 2C9 inhibitors. The lowest starting dosage of fluvastatin is recommended, then titrated as needed based on clinical response and tolerance. Alternatively, pravastatin is not metabolized by CYP450 2C9 and may be a reasonable substitute for fluvastatin. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/172920/', '[1] Kantola T, Backman JT, Niemi M, Kivisto KT, Neuvonen PJ "Effect of fluconazole on plasma fluvastatin and pravastatin concentrations." Eur J Clin Pharmacol 56 (2000): 225-9[2] "Product Information. Lescol (fluvastatin)." Novartis Pharmaceuticals, East Hanover, NJ.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172733, 'Fluvoxamine', 'Toremifene', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of toremifene, which is primarily metabolized by the isoenzyme. Because toremifene is associated with dose- and concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'Clinical and laboratory monitoring for altered efficacy and safety of toremifene may be appropriate whenever a CYP450 3A inhibitor is added to or withdrawn from therapy.', NULL, 'Clinical and laboratory monitoring for altered efficacy and safety of toremifene may be appropriate whenever a CYP450 3A inhibitor is added to or withdrawn from therapy. Complete blood counts, electrolyte levels (calcium, magnesium, potassium), and liver function tests should be obtained periodically. Patients should be advised to contact their physician if they experience vaginal bleeding or potential signs of blood clots such as chest pain, shortness of breath, sudden loss of vision, and pain, redness or swelling in an extremity. Patients should seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/172921/', '[1] "Product Information. Fareston (toremifene)." Schering Laboratories, Kenilworth, NJ.[2] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.[3] "Product Information. Fareston (toremifene)." Schering Laboratories, Kenilworth, NJ.[4] Therapeutic Research Faculty "Natural Medicines Comprehensive Database. Available from: URL: http://www.naturaldatabase.com." ([1995-2008...]):', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172734, 'Fosamprenavir', 'Toremifene', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of toremifene, which is primarily metabolized by the isoenzyme. Because toremifene is associated with dose- and concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'The use of toremifene in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided.', NULL, 'The use of toremifene in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided. If treatment with one of these agents is required, interruption of toremifene therapy should be considered. Otherwise, patients who require concomitant treatment should be closely monitored. In patients at increased risk, electrocardiograms (ECGs) should be obtained at baseline and as clinically indicated. Complete blood counts, electrolyte levels (calcium, magnesium, potassium), and liver function tests should also be obtained periodically. Patients should be advised to contact their physician if they experience vaginal bleeding or potential signs of blood clots such as chest pain, shortness of breath, sudden loss of vision, and pain, redness or swelling in an extremity. Patients should seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/172922/', '[1] "Product Information. Fareston (toremifene)." Schering Laboratories, Kenilworth, NJ.[2] "Product Information. Fareston (toremifene)." Schering Laboratories, Kenilworth, NJ.[3] Therapeutic Research Faculty "Natural Medicines Comprehensive Database. Available from: URL: http://www.naturaldatabase.com." ([1995-2008...]):[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363026);
