INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172855, 'Eslicarbazepine', 'Torasemide', 'Moderate', 'Coadministration with other medications that can lower serum sodium levels such as diuretics, selective serotonin reuptake inhibitors (SSRIs), and serotonin-noradrenaline reuptake inhibitors (SNRIs) may potentiate the risk of hyponatremia associated with eslicarbazepine.', NULL, 'Routine monitoring of serum sodium and chloride levels should be considered during maintenance treatment with eslicarbazepine acetate and concomitant use of other medications known to decrease serum sodium levels.', NULL, 'Routine monitoring of serum sodium and chloride levels should be considered during maintenance treatment with eslicarbazepine acetate and concomitant use of other medications known to decrease serum sodium levels. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hyponatremia such as nausea, vomiting, headache, malaise, lethargy, irritability, difficulty concentrating, memory impairment, confusion, weakness, muscle spasm, and unsteadiness (which may lead to falls). More severe and/or acute cases may include hallucination, syncope, seizure, coma, respiratory arrest, and death. Depending on the severity of hyponatremia, dosage reduction or discontinuation of eslicarbazepine acetate and other medications may be required, and appropriate medical intervention instituted as necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173043/', '[1] "Product Information. Aptiom (eslicarbazepine)." Sunovion Pharmaceuticals Inc, Marlborough, MA.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172856, 'Esmolol', 'Torasemide', 'Moderate', 'Although they are often combined in clinical practice, diuretics and beta-blockers may increase the risk of hyperglycemia and hypertriglyceridemia in some patients, especially in patients with diabetes or latent diabetes. In addition, the risk of QT interval prolongation and arrhythmias (e.g. torsades de pointes) due to sotalol may be increased by potassium-depleting diuretics.', NULL, 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration.', NULL, 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration. Patients should be advised to seek medical assistance if they experience dizziness, weakness, fainting, fast or irregular heartbeats, or loss of blood glucose control.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173044/', '[1] Dean S, Kendall MJ, Potter S, Thompson MH, Jackson DA "Nadolol in combination with indapamide and xipamide in resistant hypertensives." Eur J Clin Pharmacol 28 (1985): 29-33[2] Dornhorst A, Powell SH, Pensky J "Aggravation by propranolol of hyperglycaemic effect of hydrochlorothiazide in type II diabetics without alteration of insulin secretion." Lancet 1 (1985): 123-6[3] "Product Information. Lozol (indapamide)." Rhone-Poulenc Rorer, Collegeville, PA.[4] Marcy TR, Ripley TL "Aldosterone antagonists in the treatment of heart failure." Am J Health Syst Pharm 63 (2006): 49-58[5] Roux A, Le Liboux A, Delhotal B, Gaillot J, Flouvat B "Pharmacokinetics in man of acebutolol and hydrochlorothiazide as single agents and in combination." Eur J Clin Pharmacol 24 (1983): 801-6', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172857, 'Esomeprazole', 'Torasemide', 'Moderate', 'Chronic use of proton pump inhibitors (PPIs) may induce hypomagnesemia, and the risk may be increased during concomitant use of diuretics or other agents that can cause magnesium loss. Serious adverse events include tetany, seizures, tremor, carpopedal spasm, atrial fibrillation, supraventricular tachycardia, and abnormal QT interval; however, patients do not always exhibit these symptoms. Hypomagnesemia can also cause impaired parathyroid hormone secretion, which may lead to hypocalcemia. In approximately 25% of the cases of PPI-associated hypomagnesemia reviewed by the FDA, the condition did not resolve with magnesium supplementation alone but also required discontinuation of the PPI.', NULL, 'Monitoring of serum magnesium levels is recommended prior to initiation of therapy and periodically thereafter if prolonged treatment with a proton pump inhibitor is anticipated or when combined with other agents that can cause hypomagnesemia such as diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, and tacrolimus.', NULL, 'Monitoring of serum magnesium levels is recommended prior to initiation of therapy and periodically thereafter if prolonged treatment with a proton pump inhibitor is anticipated or when combined with other agents that can cause hypomagnesemia such as diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, and tacrolimus. Patients should be advised to seek immediate medical attention if they develop potential signs and symptoms of hypomagnesemia such as palpitations, arrhythmia, muscle spasm, tremor, or convulsions. In children, abnormal heart rates may cause fatigue, upset stomach, dizziness, and lightheadedness. Magnesium replacement as well as discontinuation of the PPI may be required in some patients.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173045/', '[1] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Low magnesium levels can be associated with long-term use of proton pump inhibitor drugs (PPIs). Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm245011.htm." ([2011 Mar 2]):[2] "Product Information. Nexium (esomeprazole)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172858, 'Etelcalcetide', 'Torasemide', 'Major', 'Concomitant use of etelcalcetide with other medicines known to lower serum calcium may increase the risk of hypocalcemia. The proposed mechanism is additive reduction in serum calcium levels. Significant decreases in serum calcium may cause paraesthesias, myalgias, muscle spasms, seizures, QT interval prolongation, and ventricular arrhythmia. Etelcalcetide given alone may lead to severe hypocalcemia requiring concomitant treatment with therapies to increase serum calcium levels.', NULL, 'Close monitoring of corrected serum calcium levels is recommended if etelcalcetide is used in combination with other medications known to lower serum calcium, such as denosumab, calcitonin, foscarnet, bisphosphonates, or loop diuretics.', NULL, 'Close monitoring of corrected serum calcium levels is recommended if etelcalcetide is used in combination with other medications known to lower serum calcium, such as denosumab, calcitonin, foscarnet, bisphosphonates, or loop diuretics. It is recommended that the patient''s corrected serum calcium level should be measured within one week of initiation or dose adjustment of etelcalcetide and monitored approximately every 4 weeks. In addition, the manufacturer product labeling should be consulted for management guidelines in the event of a reduction in corrected serum calcium levels below the lower limit of the normal range and/or symptoms of hypocalcemia. Patients should also be advised to contact their health care professional if they experience symptoms of hypocalcemia, such as numbness or tingling in the fingers, toes, or around the mouth, muscle spasms, twitches or cramps, seizures, confusion, and irregular heartbeats.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173046/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Skelid (tilundronate)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172859, 'Ethotoin', 'Torasemide', 'Minor', 'Some hydantoins may reduce the gastrointestinal absorption of some loop diuretics. Serum diuretic concentrations and diuretic effects after oral dosing may be decreased. Data are available for furosemide and phenytoin only. Increases in diuretic dosage may be necessary.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173047/', '[1] Ahmad S "Renal insensitivity to frusemide caused by chronic anticonvulsant therapy." Br Med J 3 (1974): 657-9[2] Fine A, Henderson IS, Morgan DR "Malabsorption of frusemide caused by phenytoin." Br Med J 2 (1977): 1601-2[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Ozuna J, Friel P "Effect of enteral tube feeding on serum phenytoin levels." J Neurosurg Nurs 16 (1984): 289-91[5] Fleisher D, Sheth N, Kou JH "Phenytoin interaction with enteral feedings administered through nasogastric tubes." JPEN J Parenter Enteral Nutr 14 (1990): 513-6[6] Krueger KA, Garnett WR, Comstock TJ, Fitzsimmons WE, Karnes HT, Pellock JM "Effect of two administration schedules of an enteral nutrient formula on phenytoin bioavailability." Epilepsia 28 (1987): 706-12[7] "Product Information. Dilantin (phenytoin)." Parke-Davis, Morris Plains, NJ.[8] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[9] Doak KK, Haas CE, Dunnigan KJ, et al. "Bioavailability of phenytoin acid and phenytoin sodium with enteral feedings." Pharmacotherapy 18 (1998): 637-45[10] Rodman DP, Stevenson TL, Ray TR "Phenytoin malabsorption after jejunostomy tube delivery." Pharmacotherapy 15 (1995): 801-5[11] Sellers EM,