INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172936, 'Torasemide', 'Polythiazide', 'Moderate', 'The combination of a thiazide and loop diuretic may produce additive or synergistic effects on diuresis and excretion of electrolytes including sodium, potassium, magnesium, and chloride. Although these agents may be combined therapeutically in some patients with inadequate response to a single agent, the increased risk of dehydration, hypotension, hypokalemia, hypomagnesemia, and hyponatremia should be recognized.', NULL, 'Caution is advised during concomitant use of a thiazide and loop diuretic.', NULL, 'Caution is advised during concomitant use of a thiazide and loop diuretic. Dosages should be titrated slowly and carefully, and electrolytes, BUN, fluid status, blood pressure, and renal function should be monitored regularly. Patients should be advised to contact their physician if they experience signs and symptoms of fluid and electrolyte depletion such as dizziness, lightheadedness, dry mouth, thirst, fatigue, weakness, lethargy, muscle cramps, decreased urination, postural hypotension, and tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173124/', '[1] Leary WP, Reyes AJ "Drug interactions with diuretics." S Afr Med J 65 (1984): 455-61[2] Brater DC, Pressley RH, Anderson SA "Mechanisms of the synergistic combination of metolazone and bumetanide." J Pharmacol Exp Ther 233 (1985): 70-4[3] Marone C, Muggli F, Lahn W, Frey FJ "Pharmacokinetic and pharmacodynamic interaction between furosemide and metolazone in man." Eur J Clin Invest 15 (1985): 253-7[4] Nakahama H, Orita Y, Yamazaki M, et al "Pharmacokinetic and pharmacodynamic interactions between furosemide and hydrochlorothiazide in nephrotic patients." Nephron 49 (1988): 223-7[5] Wollam GL, Tarazi RC, Bravo EL, Dustan HP "Diuretic potency of combined hydrochlorothiazide and furosemide therapy in patients with azotemia." Am J Med 72 (1982): 929-38[6] Arnold WC "Efficacy of metolazone and furosemide in children with furosemide-resistant edema." Pediatrics 74 (1984): 872-5[7] Sigurd B, Olesen KH, Wennevold A "The supra-additive natriuretic effect of addition of bendroflumethiazide and bumetanide in congestive heart failure. Permutation trial tests in patients in long-term treatment with bumetanide." Am Heart J 89 (1975): 163-70[8] Haller C, Salbach P, Katus H, Kubler W "Refractory oedema in congestive heart failure: a contributory role of loop diuretics?" J Intern Med 237 (1995): 211-4[9] Fliser D, Schroter M, Neubeck M, Ritz E "Coadministration of thiazides increases the efficacy of loop diuretics even in patients with advanced renal failure." Kidney Int 46 (1994): 482-8', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172937, 'Torasemide', 'Prednisolone', 'Moderate', 'The concomitant use of corticosteroids and agents that deplete potassium (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in increased risk of hypokalemia. Corticosteroids can produce hypokalemia and other electrolyte disturbances via mineralocorticoid effects, the degree of which varies with the agent and route of administration (i.e. systemic vs. local).', NULL, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given.', NULL, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173125/', '[1] Morris GC, Egan JG, Jones MK "Hypokalaemic paralysis induced by bolus prednisolone in Graves'' disease." Aust N Z J Med 22 (1992): 312[2] Thomas TP "The complications of systemic corticosteroid therapy in the elderly." Gerontology 30 (1984): 60-5[3] "Product Information. Hydeltrasol (prednisolone)." Merck & Co, Inc, West Point, PA.[4] Ramsahoye BH, Davies SV, el-Gaylani N, Sandeman D, Scanlon MF "The mineralocorticoid effects of high dose hydrocortisone." BMJ 310 (1995): 656-7[5] Powell JR "Steroid and hypokalemic myopathy after corticosteroids for ulcerative colitis. Systemic and tropical application." Am J Gastroenterol 52 (1969): 425-32[6] Thorn GW "Clinical considerations in the use of corticosteroids." N Engl J Med 274 (1966): 775-81[7] Seale JP, Compton MR "Side-effects of corticosteroid agents." Med J Aust 144 (1986): 139-42[8] Chrousos GA, Kattah JC, Beck RW, Cleary PA "Side effects of glucocorticoid treatment. Experience of the Optic Neuritis Treatment Trial." JAMA 269 (1993): 2110-2', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172938, 'Torasemide', 'Probenecid', 'Minor', 'Some investigators suggest that probenecid may acutely decrease the pharmacologic effects of loop diuretics by competitively inhibiting their renal secretion in the proximal tubule. A marked increase in diuresis and natriuresis may then follow due to increased amount of diuretic available at renal acceptor sites once probenecid has been cleared from the urine.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173126/', '[1] Leary WP, Reyes AJ "Drug interactions with diuretics." S Afr Med J 65 (1984): 455-61[2] Velasquez MT, Wan SH, Barr JW, Maronde RF "Effect of probenecid on the natriuresis and renin release induced by bumetanide in man." J Clin Pharmacol 21 (1981): 657-62[3] Brater DC, Chennavasin P "Effect of probenecid on response to bumetanide in man." J Clin Pharmacol 21 (1981): 311-5[4] Brater DC "Effects of probenecid on furosemide response." Clin Pharmacol Ther 24 (1978): 548-54[5] Vree TB, van den Biggelaar-Martea M, Verwey-van Wissen CP "Probenecid inhibits the renal clearance of frusemide and its acyl glucuronide." Br J Clin Pharmacol 39 (1995): 692-5[6] Odlind B, Beermann B "Renal tubular secretion and effects of furosemide." Clin Pharmacol Ther 27 (1980): 784-90[7] Brater DC, Leinfelder J, Anderson SA "Clinical pharmacology of torasemide, a new loop diuretic." Clin Pharmacol Ther 42 (1987): 187-92[8] Homeida M, Roberts C, Branch RA "Influence of probenecid and spironolactone on furosemide kinetics and dynamics in man." Clin Pharmacol Ther 22 (1977): 402-9[9] Brater DC, Fox WR, Chennavasin P "Interaction studies with bumetanide and furosemide: effects of probenecid and of indomethacin on response to bumetanide in man." J Clin Pharmacol 21 (1981): 647-53[10] Sommers DK, Meyer EC, Moncrieff J "The influence of co-administered organic acids on the kinetics and dynamics of frusemide." Br J Clin Pharmacol 32 (1991): 489-93[11] Honari J, Blair AD, Cutler RE "Effects of probenecid on furosemide kinetics and natriuresis in man." Clin Pharmacol Ther 22 (1977): 395-401[12] Chennavasin P, Seiwell R, Brater DC, Liang WM "Pharmacodynamic analysis of the furosemide-probenecid interaction in man." Kidney Int 16 (1979): 187-95', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172939, 'Torasemide', 'Promazine', 'Moderate', 'Some neuroleptic agents may cause prolongation of the QT interval. While clinical data are lacking, the coadministration of other agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes. In addition, neuroleptic agents may potentiate the hypotensive effect of diuretics secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during the initial dose titration period of neuroleptic therapy.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with a neuroleptic. Close clinical monitoring for development of hypotension is recommended if neuroleptic agents are prescribed with a diuretic medication. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. A lower starting dosage and slower titration of the neuroleptic agent may be appropriate in patients receiving antihypertensive therapy, especially if they are elderly.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173127/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172940, 'Torasemide', 'Propiomazine', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173128/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[3] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, T