INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172973, 'Zafirlukast', 'Torasemide', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of torsemide. The interaction has not been evaluated in pharmacokinetic studies; however, torsemide has been shown to undergo metabolism by CYP450 2C9.', NULL, 'Caution is advised when torsemide is prescribed with CYP450 2C9 inhibitors.', NULL, 'Caution is advised when torsemide is prescribed with CYP450 2C9 inhibitors. Patients should be monitored for potentially increased adverse effects such as dehydration, hypotension, electrolyte imbalances, hyperglycemia, tinnitus, and hearing loss.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173161/', '[1] Vormfelde SV, Engelhardt S, Zirk A, et al. "CYP2C9 polymorphisms and the interindividual variability in pharmacokinetics and pharmacodynamics of the loop diuretic drug torsemide." Clin Pharmacol Ther 76 (2004): 557-66[2] Miners JO, Rees DLP, Valente L, Veronese ME, Birkett DJ "Human hepatic cytochrome p450 2c9 catalyzes the rate-limiting pathway of torsemide metabolism." J Pharmacol Exp Ther 272 (1995): 1076-81[3] "Product Information. Demedex (torsemide)." Boehringer Mannheim, Gaithersburg, MD.[4] Miners JO, Birkett DJ "Cytochrome P4502C9: an enzyme of major importance in human drug metabolism." Br J Clin Pharmacol 45 (1998): 525-38[5] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172974, 'Zaleplon', 'Torasemide', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173162/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991): 390-1[4] Pacher P, Kecskemeti V "Cardiovascular side effects of new antidepressants and antipsychotics: new drugs, old concerns?" Curr Pharm Des 10 (2004): 2463-75[5] Cerner Multum, Inc. "Australian Product Information