INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (173066, 'Trabectedin', 'Ketoconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of trabectedin, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of trabectedin with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of trabectedin with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of trabectedin during and for 2 weeks after treatment with itraconazole. If a potent CYP450 3A4 inhibitor is required for short-term use (i.e., less than 14 days), it is advisable to administer the CYP450 3A4 inhibitor one week after the trabectedin infusion and discontinue it the day prior to the next infusion. Close monitoring for toxicities such as myelosuppression, rhabdomyolysis, hepatotoxicity, and cardiomyopathy is recommended during long-term coadministration, and the trabectedin dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173254/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Yondelis (trabectedin)." Janssen Pharmaceuticals, Titusville, NJ.[4] Machiels JP, Staddon A, Herremans C, et al. "Impact of cytochrome P450 3A4 inducer and inhibitor on the pharmacokinetics of trabectedin in patients with advanced malignancies: open-label, multicenter studies." Cancer Chemother Pharmacol 74 (2014): 729-37[5] "Product Information. Sporanox (i