INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (173457, 'Valdecoxib', 'Treprostinil', 'Moderate', 'Anticoagulants, antiplatelet agents, and thrombolytics may potentiate the risk of gastrointestinal bleeding complications associated with the use of nonsteroidal anti-inflammatory drugs (NSAIDs) including COX-2 inhibitors.', NULL, 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents.', NULL, 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents. Gastroprotective agents such as H2-receptor antagonists or proton pump inhibitors may be appropriate in high risk patients.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173645/', '[1] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (173458, 'Vemurafenib', 'Treprostinil', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 may increase the plasma concentrations of treprostinil, which is primarily metabolized by the isoenzyme. However, it has not been determined if the safety and efficacy of treprostinil administered subcutaneously or intravenously are altered by this interaction.', NULL, 'Pharmacologic response to treprostinil should be monitored more closely whenever a CYP450 2C8 inhibitor is added to or withdrawn from therapy, and the treprostinil dosage adjusted as necessary.', NULL, 'Pharmacologic response to treprostinil should be monitored more closely whenever a CYP450 2C8 inhibitor is added to or withdrawn from therapy, and the treprostinil dosage adjusted as necessary. Patients should be advised to notify their physician if they experience excessive adverse effects of treprostinil such as dizziness, lightheadedness, flushing, diarrhea, edema, and unusual bleeding or bruising.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173646/', '[1] "Product Information. Remodulin (treprostinil)." United Therapeutics Corp, Silver Spring, MD.', 1767363026);
