INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (173480, 'Fluconazole', 'Tretinoin', 'Major', 'Coadministration with azole antifungal agents may increase the plasma concentrations and toxicities of tretinoin. The proposed mechanism is inhibition of CYP450 2C9 and/or 3A4, two of the isoenzymes responsible for the metabolic clearance of tretinoin. All azole antifungal agents can inhibit CYP450 3A4, with itraconazole and ketoconazole considered particularly potent inhibitors.', NULL, 'Caution is advised when tretinoin is prescribed in combination with azole antifungal agents such as fluconazole, itraconazole, ketoconazole, and voriconazole.', NULL, 'Caution is advised when tretinoin is prescribed in combination with azole antifungal agents such as fluconazole, itraconazole, ketoconazole, and voriconazole. Patients should be closely monitored and advised to seek medical attention immediately if they develop early symptoms of pseudotumour cerebri such as headache, nausea, vomiting, visual disturbances, photosensitivity, and tinnitus.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173668/', '[1] Adamson PC "Pharmacokinetics of all-trans-retinoic acid: clinical implications in acute promyelocytic leukemia." Semin Hematol 31 (1994): 14-7[2] "Product Information. Vesanoid (tretinoin)." Roche Laboratories, Nutley, NJ.[3] Muindi JRF, Young CW, Warrell RP "Clinical pharmacology of all-trans retinoic acid." Leukemia 8 (1994): 1807-12[4] Lotan Y, Lotan R "Prevention of bladder cancer recurrence by retinoic acid-ketoconazole: a promising strategy?" Cancer Biol Ther 7 (2008): 101-2[5] Cordoba R, Ramirez E, Lei SH, et al. "Hypercalcemia due to an inte