    ) VALUES (1427836, 'Acetaminophen', 'Mestranol', 'Minor', 'Oral contraceptives may decrease or delay the effects of acetaminophen by increasing glucuronidation. With the usual therapeutic dosage, concurrent administration probably results in no significant additional risk.', NULL, '-', NULL, '-', 'Antagonism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
