    ) VALUES (1428006, 'Prucalopride', 'Mestranol', 'Moderate', 'RECOMMENDED: The efficacy of oral contraceptives may be reduced if severe diarrhea occurs with prucalopride treatment.', NULL, 'In patients who develop severe diarrhea during treatment with prucalopride, the use of an additional contraceptive method is recommended to prevent possible failure of oral contraception.', NULL, 'In patients who develop severe diarrhea during treatment with prucalopride, the use of an additional contraceptive method is recommended to prevent possible failure of oral contraception. If severe or persistent diarrhea occurs, patients should be advised to stop taking prucalopride and consult their physician.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
