        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1428029, 'Temazepam', 'Mestranol', 'Minor', 'Oral contraceptives may increase the clearance rate of benzodiazepines that undergo glucuronidation (such as lorazepam, oxazepam, and temazepam). Close observation for signs and symptoms of altered benzodiazepine effect is recommended.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
