INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1428037, 'Tipranavir', 'Mestranol', 'Moderate', 'Use of tipranavir in combination with estrogens may be associated with an increased risk of skin rash. The mechanism has not been described. Mild to moderate rashes including urticarial rash, maculopapular rash, and possible photosensitivity have been reported in 14% of females and 8% to 10% of males receiving tipranavir in combination with low-dose ritonavir in phase 2 and 3 trials.', NULL, 'Women who use estrogens should be advised of a potentially increased risk of skin rash during concomitant treatment with tipranavir.', NULL, 'Women who use estrogens should be advised of a potentially increased risk of skin rash during concomitant treatment with tipranavir. If a rash occurs, it is usually mild to moderate. Nevertheless, patients should be instructed to contact their physician promptly, as a temporary discontinuation of tipranavir or the estrogen may be necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
