        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1428392, 'Fluoxetine', 'Procarbazine', 'Major', 'By inhibiting serotonin metabolism, monoamine oxidase inhibitors (MAOIs) may potentiate the pharmacologic activity of selective serotonin reuptake inhibitors (SSRIs) and increase the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors.', NULL, 'In general, SSRIs should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', NULL, 'In general, SSRIs should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with SSRIs, and vice versa.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
