INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (174804, 'Enzalutamide', 'Valbenazine', 'Major', 'Coadministration of valbenazine with strong CYP450 3A4 inducers may decrease the plasma concentrations and therapeutic effects of valbenazine and its active metabolite, which are substrates of the isoenzyme. The proposed mechanism is induction of CYP450 3A4-mediated metabolism of valbenazine.', NULL, 'The use of valbenazine with strong CYP450 3A4 inducers should generally be avoided.', NULL, 'The use of valbenazine with strong CYP450 3A4 inducers should generally be avoided. If concomitant use is required, caution and close clinical and laboratory monitoring are recommended. Dosage adjustments may be required whenever a strong CYP450 3A4 inducer is added to or withdrawn from therapy. Alternative treatment may be considered if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/175002/', '[1] "Product Information. Ingrezza (valbenazine)." Neurocrine Biosciences, Inc., San Diego, CA.[2] "Product Information. Ingrezza (valbenazine)." Neurocrine Biosciences, Inc., San Diego, CA.[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767365209);
