        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1428964, 'Omadacycline', 'Lithium carbonate', 'Minor', 'Coadministration with tetracyclines may increase the plasma concentrations of lithium and the risk of toxicity. In addition, cases of pseudotumor cerebri have been reported following administration of minocycline with lithium. The mechanism is unknown; however, a proposed mechanism suggests that tetracyclines may adversely affect the renal clearance of lithium. The clinical relevance of this interaction has not been determined. A dosage adjustment for lithium may be required in patients who must take both drugs concomitantly and patients should be monitored for clinical and laboratory evidence of lithium toxicity. Patients should be advised to notify their doctor if they experience symptoms of possible lithium toxicity such as drowsiness, dizziness, weakness, ataxia, vomiting, diarrhea, thirst, blurry vision, tinnitus, tremor, or increased urination.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
