INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (175218, 'Vasopressin', 'Vemurafenib', 'Major', 'Vemurafenib can cause concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of vemurafenib with other drugs that can prolong the QT interval is not recommended.', NULL, 'Coadministration of vemurafenib with other drugs that can prolong the QT interval is not recommended. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting vemurafenib therapy and after dose modification.', 'Synergism', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/175427/', '[1] "Product Information. Zelboraf (vemurafenib)." Genentech, South San Francisco, CA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (175219, 'Venlafaxine', 'Vasopressin', 'Moderate', 'The antidiuretic response to vasopressin or desmopressin may be potentiated by certain drugs, including nonsteroidal anti-inflammatory agents, selective serotonin reuptake inhibitors, tricyclic/tetracyclic antidepressants, carbamazepine, chlorpropamide, chlorpromazine, clofibrate, eslicarbazepine, fludrocortisone, haloperidol, lamotrigine, oxcarbazepine, urea, and some antineoplastic agents (e.g., vinca alkaloids, cisplatin, cyclophosphamide). These drugs can occasionally cause fluid retention, in some cases secondary to the syndrome of inappropriate antidiuretic hormone secretion (SIADH). Coadministration with vasopressin or desmopressin may, therefore, increase the risk of water intoxication and/or hyponatremia. Seizure and coma in association with severe hyponatremia have been reported during concomitant use of desmopressin and imipramine or ibuprofen.', NULL, 'Caution is advised when vasopressin or desmopressin is used in combination with drugs that can cause fluid retention or SIADH.', NULL, 'Caution is advised when vasopressin or desmopressin is used in combination with drugs that can cause fluid retention or SIADH. Some authorities recommend adjusting vasopressin dosage as needed. Serum electrolytes, especially sodium, as well as BUN and creatinine should be monitored regularly. Patients should be advised to seek medical attention if they develop signs and symptoms of water intoxication or hyponatremia such as anorexia, nausea, vomiting, headache, malaise, lethargy, irritability, difficulty concentrating, memory impairment, confusion, weakness, muscle spasm, unsteadiness (which may lead to falls), anuria, and weight gain. Severe hyponatremia can be life-threatening if it is not promptly diagnosed and treated; therefore, early treatment is important to help prevent progression to seizures, coma, respiratory arrest, and death.', 'Synergism', 'Phenelzine, Tranylcypromine, St. John''s Wort, Nefazodone, Isocarboxazid, Oxitriptan, Viloxazine, Tryptophan, Bupropion, Esketamine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/175428/', '[1] Vanhees SL, Paridaens R, Vansteenkiste JF "Syndrome of inappropriate antidiuretic hormone associated with chemotherapy-induced tumour lysis in small-cell lung cancer: case report and literature review." Ann Oncol 11 (2000): 1061-5[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Kosmidis HV, Bouhoutsou DO, Varvoutsi MC, et al. "Vincristine overdose: experience with 3 patients." Pediatr Hematol Oncol 8 (1991): 171-8[4] Maramattom BV "Duloxetine-induced syndrome of inappropriate antidiuretic hormone secretion and seizures." Neurology 66 (2006): 773-4[5] ten Holt WL, van Iperen CE, Schrijver G, Bartelink AK "Severe hyponatremia during therapy with fluoxetine." Arch Intern Med 156 (1996): 681-2[6] Miller MG "Tricyclics as a possible cause of hyponatremia in psychiatric patients." Am J Psychiatry 146 (1989): 807[7] Petersson I, Nilsson G, Hansson B-G, Hedner T "Water intoxication associated with non-steroidal anti-inflammatory drug therapy." Acta Med Scand 221 (1987): 221-3[8] Nielsen OA, Johannessen AC, Bardrum B "Oxcarbazepine-induced hyponatremia, a cross-sectional study." Epilepsy Res 2 (1988): 269-71[9] Schattner A, Skurnik Y "Fluoxetine-induced SIADH." J Am Geriatr Soc 44 (1996): 1413[10] Tanay A, Firemann Z, Yust I, Abramov AL "Chlorpropamide-induced syndrome of inappropriate antidiuretic hormone secretion." J Am Geriatr Soc 29 (1981): 334-6[11] Catalano G, Kanfer SN, Catalano MC, Alberts VA "The role of sertraline in a patient with recurrent hyponatremia." Gen Ho