INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (175359, 'Vemurafenib', 'Tolbutamide', 'Moderate', 'Coadministration with vemurafenib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 2C8. The proposed mechanism, based on in vitro data, is decreased clearance due to vemurafenib-mediated inhibition of CYP450 2C8.', NULL, 'Caution is advised if vemurafenib is used concomitantly with drugs that are substrates of CYP450 2C8, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if vemurafenib is used concomitantly with drugs that are substrates of CYP450 2C8, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever vemurafenib is added to or withdrawn from therapy with these drugs. Since vemurafenib has a long half-life, a wash-out period of 8 days following the discontinuation of vemurafenib has been suggested to avoid an interaction with subsequent therapy. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Methacholine, Hexaminolevulinate, Secretin human, Methylene blue, Sermorelin, Sorbitol, Galactose, Magnesium sulfate, Vitamin A, Metyrapone, Edrophonium, More', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Rosuvastatin, Amphotericin B, Clindamycin, Ciclopirox, Tioconazole, Brinzolamide, Clotrimazole, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/175568/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Diabinese (chlorpropamide