INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (175538, 'Fluconazole', 'Venlafaxine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 such as azole antifungal agents and ketolide/macrolide antibiotics may increase the plasma concentrations of venlafaxine and its metabolite, O-desmethylvenlafaxine (ODV). The possibility of prolonged and/or increased pharmacologic effects of venlafaxine should be considered. Venlafaxine may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval such as azole antifungal agents and ketolide/macrolide antibiotics may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended when venlafaxine is used with CYP450 3A4 inhibitors that can prolong the QT interval such as azole antifungal agents and ketolide/macrolide antibiotics.', NULL, 'Caution is recommended when venlafaxine is used with CYP450 3A4 inhibitors that can prolong the QT interval such as azole antifungal agents and ketolide/macrolide antibiotics. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/175747/', '[1] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Cha