    ) VALUES (1429002, 'Darunavir', 'Paritaprevir', 'Major', 'Coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir may decrease the plasma concentrations of darunavir. The mechanism of interaction has not been established.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of darunavir plus ritonavir and ombitasvir/paritaprevir/ritonavir plus dasabuvir is not recommended.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of darunavir plus ritonavir and ombitasvir/paritaprevir/ritonavir plus dasabuvir is not recommended.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
