        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1429008, 'Esomeprazole', 'Paritaprevir', 'Moderate', 'Coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir may decrease the plasma concentrations of omeprazole. The proposed mechanism of interaction is CYP450 2C19 induction by ritonavir.', NULL, 'Patients should be monitored for potentially diminished efficacy of omeprazole during coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir.', NULL, 'Patients should be monitored for potentially diminished efficacy of omeprazole during coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir. The dosage of omeprazole may be increased in patients whose symptoms are not well controlled; however, some authorities suggest that the dosage of omeprazole should be limited to 40 mg/day.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
