    ) VALUES (1429027, 'Paritaprevir', 'Nevirapine', 'Major', 'Coadministration with moderate to potent inducers of CYP450 isoenzymes may significantly decrease the plasma concentrations of ombitasvir, paritaprevir, and dasabuvir. The proposed mechanism may involve induction of CYP450 3A4.', NULL, 'Concomitant use of CYP450 3A4 inducers such etravirine or nevirapine with ombitasvir/paritaprevir/ritonavir with or without dasabuvir is considered contraindicated by some authorities.', NULL, 'Concomitant use of CYP450 3A4 inducers such etravirine or nevirapine with ombitasvir/paritaprevir/ritonavir with or without dasabuvir is considered contraindicated by some authorities.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
