    ) VALUES (1429039, 'Ziprasidone', 'Ritonavir', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may only modestly increase the plasma concentrations of ziprasidone, as less than 1/3 of ziprasidone metabolic clearance occurs via oxidation mediated by CYP450 3A4.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
