        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1429040, 'Abiraterone', 'Dasabuvir', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 may increase the plasma concentrations of dasabuvir, which is primarily metabolized by the isoenzyme. The risk of ALT elevations and QT prolongation may be increased.', NULL, 'Caution is advised if dasabuvir is prescribed in combination with CYP450 2C8 inhibitors.', NULL, 'Caution is advised if dasabuvir is prescribed in combination with CYP450 2C8 inhibitors. Patients should be monitored for development of hepatotoxicity (i.e., ALT elevations) and QT interval prolongation.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
