        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1429041, 'Efavirenz', 'Dasabuvir', 'Major', 'Coadministration of efavirenz-based antiretroviral regimens with ombitasvir/paritaprevir/ritonavir plus dasabuvir has been reported to be poorly tolerated and resulted in liver enzyme elevations.', NULL, 'Concomitant use of efavirenz with ombitasvir/paritaprevir/ritonavir plus dasabuvir is considered contraindicated.', NULL, 'Concomitant use of efavirenz with ombitasvir/paritaprevir/ritonavir plus dasabuvir is considered contraindicated.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
