        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1429042, 'Furosemide', 'Dasabuvir', 'Moderate', 'Coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir may increase the peak plasma concentration (Cmax) of furosemide. The mechanism of interaction has not been established.', NULL, 'Clinical monitoring of patients is recommended during concomitant use of furosemide with ombitasvir, paritaprevir, ritonavir, and dasabuvir.', NULL, 'Clinical monitoring of patients is recommended during concomitant use of furosemide with ombitasvir, paritaprevir, ritonavir, and dasabuvir. Dosage adjustment may be required based on therapeutic response.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
