        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1429043, 'Gemfibrozil', 'Dasabuvir', 'Major', 'Coadministration with potent inhibitors of CYP450 2C8 may significantly increase the plasma concentrations of dasabuvir, which is primarily metabolized by the isoenzyme. When a single dose of dasabuvir was administered to 11 study subjects treated with the potent CYP450 2C8 inhibitor gemfibrozil at 600 mg twice daily, dasabuvir peak plasma concentration (Cmax) and systemic exposure (AUC) increased by approximately 2- and 11-fold, respectively.', NULL, 'Concomitant use of dasabuvir with potent CYP450 2C8 inhibitors is considered contraindicated.', NULL, 'Concomitant use of dasabuvir with potent CYP450 2C8 inhibitors is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
