        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1429048, 'Anisindione', 'Rifabutin', 'Major', 'Rifampin may decrease the anticoagulant effect of warfarin by enhancing CYP450 hepatic microsomal enzyme metabolism of warfarin.', NULL, 'Concurrent use should be avoided unless no alternatives are available.', NULL, 'Concurrent use should be avoided unless no alternatives are available. The INR or prothrombin time should be monitored closely during concomitant therapy and after rifampin is discontinued. Adjustments in warfarin dosage may be needed when rifampin dosage is added, discontinued, or changed. This interaction may occur with other oral anticoagulants and with rifabutin.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
