        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1429053, 'Clofibrate', 'Rifabutin', 'Minor', 'Rifampin may increase the metabolism of clofibrate. Serum concentration and therapeutic efficacy of clofibrate may be reduced. Theoretically, a similar reaction may occur with rifabutin, although data are lacking. Higher dosages of clofibrate may be required to sustain an adequate lipid-lowering effect, and more frequent monitoring of serum levels may be needed.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
