    ) VALUES (1429057, 'Digitoxin', 'Rifabutin', 'Moderate', 'Rifampin may decrease the plasma concentrations of digitoxin by inducing its CYP450 hepatic metabolism.', NULL, 'While receiving rifampin, the patient should be monitored for inadequate response to digitoxin.', NULL, 'While receiving rifampin, the patient should be monitored for inadequate response to digitoxin. Dosage should be increased as necessary. The clinician should consider monitoring the patient for arrhythmia control, signs and symptoms of heart failure, and serum digitoxin concentrations.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
