    ) VALUES (1429079, 'Fesoterodine', 'Rifabutin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of fesoterodine''s active metabolite, 5-hydroxymethyl tolterodine, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised when fesoterodine is used with CYP450 3A4 inducers.', NULL, 'Caution is advised when fesoterodine is used with CYP450 3A4 inducers. The possibility of diminished therapeutic effects should be considered.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
