    ) VALUES (1429080, 'Flibanserin', 'Rifabutin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of flibanserin, which is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19.', NULL, 'Concomitant use of flibanserin with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of flibanserin with potent CYP450 3A4 inducers should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
