        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1429081, 'Fludrocortisone', 'Rifabutin', 'Moderate', 'Rifampin may induce the hepatic metabolism of corticosteroids, possibly reducing their therapeutic effect. The elimination half-life of corticosteroids has been shown to be reduced by up to 45% when rifampin is coadministered.', NULL, 'Patients should be monitored for altered corticosteroid effects.', NULL, 'Patients should be monitored for altered corticosteroid effects. Dosage increases may be necessary. Two- to threefold increases in prednisolone dosage have been recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
