    ) VALUES (1429085, 'Fosphenytoin', 'Rifabutin', 'Moderate', 'Rifampin and rifabutin may decrease plasma concentrations and effectiveness of hydantoins by inducing their CYP450 hepatic metabolism.', NULL, 'Close observation for clinical and laboratory evidence of altered hydantoin effect is recommended.', NULL, 'Close observation for clinical and laboratory evidence of altered hydantoin effect is recommended. Patients should be advised to notify their physician if they experience loss of seizure control. Dosage should be increased as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
