        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1429087, 'Gadoxetic acid', 'Rifabutin', 'Moderate', 'Anionic drugs such as rifamycins may interfere with the hepatic contrast enhancement of gadoxetate by competing for biliary excretion. In animal studies, rifampin blocked the hepatic uptake and decreased the hepatic contrast effect of gadoxetate. The interaction has not been studied in human.', NULL, 'Clinicians should be aware of possible interference with gadoxetate imaging in patients receiving rifamycin therapy.', NULL, 'Clinicians should be aware of possible interference with gadoxetate imaging in patients receiving rifamycin therapy.', 'Absorption, Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
