    ) VALUES (1429097, 'Haloperidol', 'Rifabutin', 'Major', 'Rifampin and other strong CYP450 3A4 inducers may significantly decrease serum concentrations of haloperidol. The mechanism may be related to induction of the CYP450 3A4 metabolism of haloperidol.', NULL, 'Close observation for reduced clinical effects is recommended if these drugs must be used together.', NULL, 'Close observation for reduced clinical effects is recommended if these drugs must be used together. It may be necessary to adjust haloperidol dosage and/or dosage interval whenever a strong CYP450 3A4 inducer is added to or discontinued from therapy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
