    ) VALUES (1429104, 'Indium In-111 oxyquinoline', 'Rifabutin', 'Moderate', 'Certain medications may decrease chemotaxis and cause false-negative results with Indium In-111-labeled leukocyte studies.', NULL, 'Clinicians should be aware of possible diagnostic interference in patients who are on long-term antibiotic treatment, corticosteroids, interleukin-2, parenteral nutrition, or high doses or supratherapeutic concentrations of lidocaine or procainamide.', NULL, 'Clinicians should be aware of possible diagnostic interference in patients who are on long-term antibiotic treatment, corticosteroids, interleukin-2, parenteral nutrition, or high doses or supratherapeutic concentrations of lidocaine or procainamide.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
