    ) VALUES (1429107, 'Isavuconazonium', 'Rifabutin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of isavuconazole, which is primarily metabolized by CYP450 3A4 and 3A5 and subsequently by uridine diphosphate glucuronosyltransferases (UGT).', NULL, 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inducers is considered contraindicated.', NULL, 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
