    ) VALUES (1429109, 'Isosorbide dinitrate', 'Omeprazole', 'Minor', 'Omeprazole may inhibit the drug delivery of oral nitrates. Antianginal effects may be diminished, and myocardial ischemia may be exacerbated. If an interaction is suspected, alternate acid-suppressant therapy may be considered.', NULL, '-', NULL, '-', 'Distribution', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
