    ) VALUES (1429110, 'Istradefylline', 'Rifabutin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of istradefylline, which is primarily metabolized by CYP450 3A4 and 1A1.', NULL, 'The use of istradefylline in combination with potent CYP450 3A4 inducers is not recommended.', NULL, 'The use of istradefylline in combination with potent CYP450 3A4 inducers is not recommended. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
