    ) VALUES (1429133, 'Rifabutin', 'Medroxyprogesterone acetate', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of medroxyprogesterone, which is primarily metabolized by the isoenzyme.', NULL, 'Pharmacologic response to medroxyprogesterone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', NULL, 'Pharmacologic response to medroxyprogesterone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary. When administered as the depot formulation for contraception, no dosage adjustment for medroxyprogesterone is currently recommended during coadministration with CYP450 3A4 inducers.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
