    ) VALUES (1429141, 'Rifabutin', 'Midostaurin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of midostaurin and its active metabolites, which are all substrates of the isoenzyme.', NULL, 'The potential for diminished pharmacologic effects of midostaurin should be considered during coadministration with CYP450 3A4 inducers.', NULL, 'The potential for diminished pharmacologic effects of midostaurin should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
