    ) VALUES (1429145, 'Rifabutin', 'Montelukast', 'Moderate', 'Coadministration with inducers of hepatic CYP450 isoenzymes may decrease the plasma concentrations of montelukast, which is metabolized by CYP450 2C8, 2C9, and 3A4 .', NULL, 'No dosage adjustment for montelukast is recommended when used concomitantly with CYP450 inducers.', NULL, 'No dosage adjustment for montelukast is recommended when used concomitantly with CYP450 inducers. However, clinical monitoring for potentially reduced therapeutic effects may be appropriate, especially for potent inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
