    ) VALUES (1429146, 'Rifabutin', 'Naldemedine', 'Moderate', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of naldemedine, which is primarily metabolized by CYP450 3A4 to nor-naldemedine and to a minor extent by UGT1A3 to naldemedine 3-G. Both metabolites have demonstrated antagonistic activity for opioid receptors, but with less potency than the parent drug.', NULL, 'Caution and monitoring for potential loss of efficacy is recommended with the concomitant use of naldemedine with moderate CYP450 3A4 inducers.', NULL, 'Caution and monitoring for potential loss of efficacy is recommended with the concomitant use of naldemedine with moderate CYP450 3A4 inducers.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
