    ) VALUES (1429162, 'Rifabutin', 'Ondansetron', 'Moderate', 'Concomitant use of rifamycin derivatives with ondansetron may result in a reduced antiemetic effect, particularly after the oral administration of ondansetron. The proposed mechanism is induction of the CYP450 3A4/1A2 mediated metabolism of ondansetron.', NULL, 'Patients should be monitored for reduced antiemetic effects and the ondansetron dose adjusted as necessary.', NULL, 'Patients should be monitored for reduced antiemetic effects and the ondansetron dose adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
