        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1429230, 'Telaprevir', 'Rifabutin', 'Major', 'Coadministration of rifabutin with boceprevir or telaprevir is expected to increase the plasma concentrations of rifabutin and its metabolite and decrease the plasma concentrations of the hepatitis C virus NS3/4A protease inhibitor. The proposed mechanism involves inhibition of rifabutin metabolism via CYP450 3A4 and, conversely, induction of boceprevir and telaprevir metabolism via the same isoenzyme.', NULL, 'Safe and effective dosages have not been established for the concomitant use of rifabutin with either boceprevir or telaprevir.', NULL, 'Safe and effective dosages have not been established for the concomitant use of rifabutin with either boceprevir or telaprevir. Coadministration is not recommended.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
