        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1429244, 'Troleandomycin', 'Rifabutin', 'Moderate', 'The coadministration of clarithromycin and rifabutin at normally recommended dosages has been reported to have resulted in significantly altered pharmacokinetics for both drugs. This bidirectional interaction is consistent with rifabutin''s cumulative inducing effect over time on the CYP450 enzymatic pathway as well as clarithromycin''s immediate inhibiting effect on the pathway. Other macrolide antibiotics may interact in a similar manner with rifamycins.', NULL, 'Some experts recommend that this combination be avoided since it may result in decreased efficacy of the macrolide and increased rifamycin toxicity (e.', NULL, 'Some experts recommend that this combination be avoided since it may result in decreased efficacy of the macrolide and increased rifamycin toxicity (e.g, neutropenia, uveitis).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
