    ) VALUES (1429245, 'Trovafloxacin', 'Omeprazole', 'Minor', 'Minor increases in trovafloxacin AUC and peak serum concentrations have occurred when trovafloxacin was administered with omeprazole. The mechanism has not been described. The manufacturer does not consider this pharmacokinetic drug interaction to be of clinical importance.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
