INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (176162, 'Etravirine', 'Voriconazole', 'Moderate', 'Coadministration of etravirine and voriconazole may result in increased plasma concentrations of both drugs. Etravirine is a substrate of CYP450 2C19, 2C9 and 3A4 as well as an inhibitor of CYP450 2C19 and 2C9, while voriconazole is a substrate of CYP450 2C19 and inhibitor of CYP450 2C19, 2C9 and 3A4. Theoretically, etravirine can inhibit the metabolism of voriconazole via CYP450 2C19, and voriconazole can inhibit the metabolism of etravirine via CYP450 2C19, 2C9 and 3A4.', NULL, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with voriconazole.', NULL, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with voriconazole. Dosage adjustments may be required for one or both drugs if undue adverse effects occur.', 'Metabolism', 'Micafungin, Amphotericin B, Oteseconazole, Flucytosine, Anidulafungin', 'Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Lenacapavir, Velpatasvir, Oseltamivir, Brincidofovir, Stavudine, Famciclovir, Darunavir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/176372/', '[1] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (176163, 'Everolimus', 'Voriconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and P-glycoprotein may significantly increase the plasma concentrations of everolimus following oral administration. Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus. The risk of side effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia may be increased.', NULL, 'Concomitant use of everolimus with potent inhibitors of CYP450 3A4 and P-glycoprotein should generally be avoided.', NULL, 'Concomitant use of everolimus with potent inhibitors of CYP450 3A4 and P-glycoprotein should generally be avoided. Some authorities recommend avoiding concomitant use of everolimus during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Flucytosine, Anidulafungin, Caspofungin, Oteseconazole', 'Futibatinib, Trametinib, Nintedanib, Afatinib, Dacomitinib, Tivozanib, Alectinib, Binimetinib, Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/176373/', '[1] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Homma S, Takahashi KI, Nihei S, Kato F, Sugihara S, Nunoda S "The successful management of respiratory complications with long-term, low-dose macrolide administration in pediatric heart transplant recipients." Int Heart J (2014):[5] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
