INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (176335, 'Griseofulvin', 'Vortioxetine', 'Moderate', 'Coadministration with potent inducers of CYP450 isoenzymes may significantly decrease the plasma concentrations of vortioxetine, which is primarily metabolized by CYP450 2D6.', NULL, 'An increase in the dosage of vortioxetine should be considered when used in combination with potent CYP450 inducers (e.', NULL, 'An increase in the dosage of vortioxetine should be considered when used in combination with potent CYP450 inducers (e.g., carbamazepine, phenobarbital, phenytoin, rifamycins) for greater than 14 days, up to a maximum of three times the original dosage depending on clinical response. Following discontinuation of the potent CYP450 inducer, vortioxetine dosage should be returned to the original level within 14 days. Other known CYP450 inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, enzalutamide, efavirenz, etravirine, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with vortioxetine is unknown.', 'Metabolism', 'Salicylic acid, Fluconazole, Selenium Sulfide, Thiabendazole, Miconazole, Nystatin, Ciclopirox, Ketoconazole, Econazole, Flucytosine, Tioconazole, More', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, St. John''s Wort, Milnacipran, Levomilnacipran, Doxepin, Duloxetine, Isocarboxazid, Viloxazine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/176545/', '[1] "Product Information. Brintellix (vortioxetine)." Takeda Pharmaceuticals America, Lincolnshire, IL.[2] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[3] 