        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1429329, 'Ticlopidine', 'Sodium bicarbonate', 'Minor', 'Coadministration of oral medications that contain magnesium, aluminum, calcium, or sodium (i.e., antacids) has decreased ticlopidine serum concentrations by 18%. Administration of these drugs should be separated by at least three hours.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
