        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1429335, 'Trandolapril', 'Sodium bicarbonate', 'Minor', 'Coadministration with antacids may decrease the oral bioavailability of captopril and other angiotensin converting enzyme (ACE) inhibitors due to delayed gastric emptying and/or elevated gastric pH. Based on available data, the clinical significance of this interaction appears to be minor. As a precaution, patients may want to consider separating the administration times of ACE inhibitors and antacids or oral medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) by 1 to 2 hours.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
